-- under PostgreSQL/PostGIS

DROP TABLE IF EXISTS testwkt;

CREATE TABLE testwkt (
    id    SERIAL PRIMARY KEY,
    label VARCHAR,
    geom  GEOMETRY(Geometry, 2154),
    wkt   TEXT
);

CREATE INDEX ON testwkt USING gist(geom);

SELECT populate_geometry_columns();

